import axios from 'axios';

const urls = {
        // this set up is for docker compose only
        // in case of kubernetes you need to set server to the ingress nginx 
        // 'server': "http://<ingress-service-name>.<namespace-name>.svc.cluster.local",
        'server':'http://reverse-proxy', 
        'browser':''
}


export default function buildClient(req){
    if(typeof window === 'undefined' ){
        // WE ARE ON SERVER
        return axios.create({
            baseURL:urls.server,
            headers:req.headers //  headers includes hosts cookies and all 
        });
   }else{
       // WE ARE ON BROWSER
    //    return axios.create({
    //        baseURL: urls[service].browser
    //    });
    return axios.create();
   }     
}
