import  useRequest from "../../hooks/useRequest";
import {useState,useEffect} from 'react';

const orderDetails = ({currentUser,order,networkErrors})=>{

    const [tlmeLeft,setTimeLeft] = useState((new Date(order.expiresAt)-new Date())/1000); 

    useEffect(()=>{
        setInterval(() => {
            setExpiresAt()
        }, 1000);
    },[])
    console.log('order',order);

    const {doRequest,errors} = useRequest({
        url:'/api/payments/v1/create-payment',
        method:'post',
        body:{
            orderId:order?order.id:null
        },
        onSuccess:(data)=> console.log('success',data)
    });

    return (
        networkErrors 
                ? 
                <div className="alert alert-danger" role="alert">
                    <ul className="my-0">
                        {
                            networkErrors.map((e,ind)=>{
                            return <li key={ind}>{e.message}</li>;
                            })
                        }
                    </ul>
                </div> 
                :
                <div>
                    {/* <h1>{ticket.title}</h1>
                    <h4>Price: {ticket.price}</h4>
                    {errors} */}
                    <button onClick={doRequest} className="btn btn-primary">Pay</button>
                </div>
        );
}; 




orderDetails.getInitialProps = async (context,client,currentUser)=>{
    try{
        const data = await client.get('/api/order/v1/get-order/'+context.query.orderId);
        return {order:data.data.order};
    }catch(err){
        console.log(err);
        return {networkErrors:err.response.data.errors};
    }
}

export default orderDetails;
